    west.define('west.gui.Progressbar', west.gui.Component, {
        init: function(current, max) {
            this.divMain = $('<div class="tw2gui_progressbar">' + '<div class="tw2gui_progressbar_progress">' + '<div class="tw2gui_progressbar_begin"></div>' + '<div class="tw2gui_progressbar_end"></div>' + '<div class="tw2gui_progressbar_fill"></div>' + '<div class="tw2gui_progressbar_contents"></div>' + '</div>' + '</div>');
            this.divMain[0].guiElement = this;
            this.valueIsTime = false;
            this.timeBuffWay = false;
            this.value = current;
            this.maxValue = max;
            this.endless = false;
            this.percentOnly = false;
            this.textOnly = false;
            this.color = 'green';
            this.direction = 'direction_ltr';
            this.update();
        },
        setMaxValue: function(val) {
            this.maxValue = val;
            this.update();
            return this;
        },
        setColor: function(color) {
            this.removeClass(this.color);
            this.addClass(color);
            this.color = color;
            return this;
        },
        setDirection: function(direction) {
            direction = 'direction_' + direction;
            this.removeClass(this.direction);
            this.addClass(direction);
            this.direction = direction;
            return this;
        },
        setValue: function(val) {
            this.value = val;
            this.update();
            return this;
        },
        increase: function(step) {
            this.value = this.value + (step || 1);
            this.update();
            return this;
        },
        showPercentOnly: function(bool) {
            this.percentOnly = bool;
            this.update();
            return this;
        },
        getValue: function() {
            return this.value;
        },
        update: function() {
            var calc, contents, fill;
            if (this.endless)
                calc = 100;
            else {
                calc = this.maxValue > 0 ? Math.floor((this.value / this.maxValue) * 100) : 100;
                calc = calc > 100 ? 100 : calc;
            }
            contents = this.$('div.tw2gui_progressbar_contents', this.divMain);
            fill = this.$('div.tw2gui_progressbar_fill', this.divMain);
            if (null != this.maxValue && null != this.value) {
                fill.css("width", calc + "%");
            }
            contents.empty();
            var v = this.value
              , m = this.maxValue
              , differentValue = this.differentValue;
            if (this.valueIsTime) {
                if (this.timeBuffWay) {
                    v = parseInt(v, 10).formatDurationBuffWay() || 0;
                    m = parseInt(m, 10).formatDurationBuffWay() || 0;
                } else {
                    v = parseInt(v, 10).formatDuration();
                    m = parseInt(m, 10).formatDuration();
                }
            }
            if (this.endless)
                contents.append($("<span>" + v + "</span>"));
            else if (this.percentOnly)
                contents.append($('<span>' + calc + '%</span>'))
            else if (this.textOnly)
                contents.append("<span>" + v + " / " + m + "</span>");
            else if (this.valueDifferent)
                contents.append("<span>" + differentValue + "</span>");
            else
                contents.append($('<span>' + (v + ' / ' + m + (' (' + calc + '%)')) + '</span>'));
        },
        setLabel: function(text, icon) {
            var el = this.$('.tw2gui_progressbar_label', this.divMain);
            if (el)
                el.remove();
            if (!(icon instanceof west.gui.Icon) && undefined !== icon)
                icon = new west.gui.Icon(icon);
            el = $("<div class='tw2gui_progressbar_label'></div>");
            if (undefined !== icon)
                el.append(icon.getMainDiv());
            el.append("<span>" + text + "</span>");
            this.divMain.prepend(el);
            return this;
        },
        dropShadow: function() {
            this.divMain.append($("<div class='tw2gui_progressbar_shadow'></div>"));
            return this;
        },
        setEndless: function(state) {
            this.endless = state;
            this.update();
            return this;
        },
        setTextOnly: function(state) {
            this.textOnly = state;
            this.update();
            return this;
        },
        setValueTime: function(state, buffWay) {
            this.valueIsTime = state;
            this.timeBuffWay = !!buffWay;
            this.update();
            return this;
        },
        setDifferentValue: function(value) {
            this.valueDifferent = true;
            this.differentValue = value
        }
    });
