west.define('UserMessage', west.gui.Dialog, {
    init: function(msg, type) {
        this.callParent();
        this.divMain.css({
            "cursor": "pointer",
            "max-width": "800px"
        });
        this.$(".tw2gui_dialog_text").css({
            "font-size": "20px",
            "padding": "12px 0",
            "max-width": "600px"
        });
        this.setType(undefined === type ? UserMessage.TYPE_ERROR : type).setText(msg);
    },
    getType: function() {
        return this.type;
    },
    setType: function(type) {
        if (undefined === type)
            return;
        this.type = type;
        switch (type) {
        case UserMessage.TYPE_SUCCESS:
            this.setIcon(west.gui.Dialog.SYS_OK);
            this.setTitle("Erfolgreich");
            break;
        case UserMessage.TYPE_HINT:
            this.setIcon(west.gui.Dialog.SYS_WARNING);
            this.setTitle("Hinweis");
            break;
        case UserMessage.TYPE_ERROR:
        default:
            this.setIcon(west.gui.Dialog.SYS_WARNING);
            this.setTitle("Achtung");
            break;
        }
        return this;
    },
    hide: function() {
        if (!isDefined(window.Config) || !Config.get("gui.main.animations")) {
            this.divMain.delay(2000).hide(10, function() {
                $(this).remove();
            });
        } else {
            this.divMain.delay(600).fadeOut(2000, function() {
                $(this).remove();
            });
        }
    },
    show: function() {
        var main = this.divMain;
        main.off();
        var that = this;
        main.click(function() {
            $(this).remove();
        });
        main.on('mouseenter', function() {
            if ($(this).css("opacity") >= 0.38) {
                $(this).clearQueue();
                $(this).stop();
                $(this).show();
                $(this).css("opacity", 100);
            }
        }).on('mouseleave', function() {
            that.hide();
        });
        main.trigger('mouseover');
        main.appendTo(document.body);
        var t = 20;
        var prop = {
            "left": ($(window).width() - main.width()) / 2,
            top: t
        };
        if (!isDefined(window.Config) || !Config.get("gui.main.animations")) {
            main.css(prop).trigger('mouseout');
        } else {
            main.css(jQuery.extend(prop, {
                opacity: 0.0,
                top: t - (t / 2)
            }));
            main.md_center({
                transition: 1000,
                opacity: 1.0,
                top: t
            }, function() {
                main.trigger('mouseout');
            });
        }
        return this;
    }
});
