    MessagesWindow.Telegram.send = function() {
        var to = this.receiverInput.getValue();
        var masstele = this.masstelegramInput.isSelected();
        var subject = this.subjectInput.getValue();
        var text = this.telegramInput.getContent();
        if (!to) {
            new UserMessage("Du musst einen Empfänger angeben!",UserMessage.TYPE_ERROR).show();
            return;
        }
        if (!subject || subject.length < 3) {
            new UserMessage("Der Betreff muss mindestens 3 Zeichen lang sein!",UserMessage.TYPE_ERROR).show();
            return;
        }
        if (!text || text.length < 3) {
            new UserMessage("Der Text muss mindestens 3 Zeichen lang sein!",UserMessage.TYPE_ERROR).show();
            return;
        }
        Ajax.remoteCall('messages', 'send', {
            to: to,
            subject: subject,
            text: text,
            masstelegramm: masstele
        }, function(resp) {
            if (!resp[0]) {
                if (resp[2] != null && (resp[2][0] == 'block' || resp[2][0] == 'not_found')) {
                    new west.gui.Dialog(resp[1],"Möchtest du die betreffenden Personen aus der Empfängerliste streichen?",west.gui.Dialog.SYS_QUESTION).addButton("yes", function() {
                        var ls = to.split(/;|,/);
                        var blocked = {};
                        var out = [];
                        resp[2][1].each(function(el) {
                            blocked[el.toLowerCase()] = true
                        });
                        ls.each(function(el) {
                            if (!blocked[el.trim().toLowerCase()])
                                out.push(el)
                        });
                        MessagesWindow.Telegram.receiverInput.setValue(out.join(';'));
                    }).addButton("no").show();
                } else
                    new UserMessage(resp[1],UserMessage.TYPE_ERROR).show();
                return false;
            }
            MessagesWindow.Telegram.clearFields();
            new UserMessage("Dein Telegramm wurde verschickt!",UserMessage.TYPE_ERROR);
            MessagesWindow.open();
            return true;
        }, MessagesWindow);
    }
