    MessagesWindow.Telegram.insert_to = function(type, name) {
        var input = this.receiverInput;
        var addToList = function(rec) {
            if (input.getValue() != '') {
                var names = input.getValue().split(';');
                var newNames = Array.isArray(rec) ? rec : rec.split(';');
                rec = newNames.concat(names.filter(function(name) {
                    return !~newNames.indexOf(name);
                }));
            }
            input.setValue(Array.isArray(rec) ? rec.join(';') : rec);
        };
        if (null === type) {
            addToList(name);
        } else if ('insert_friends' === type) {
            addToList(Chat.Friendslist.getFriends().map(function(client) {
                return client.pname;
            }));
        } else {
            Ajax.remoteCall('messages', type, {
                type: name
            }, function(json) {
                if (json[0]) {
                    addToList(json[1]);
                } else {
                    new UserMessage(json[1],UserMessage.TYPE_ERROR).show();
                }
            }, MessagesWindow);
        }
    }
