    InterfaceSub.prototype._initChat = function() {
        var wrap = $("<ul style='list-style-type: none;'></ul>");
        var add = function(el) {
            wrap.append($("<li style='margin: 5px 0;'></li>").append(el.getMainDiv()));
        };
        var chatsound = new west.gui.Checkbox("Chatton deaktivieren").setSelected(Config.get("soundsOff"));
        var chatfade = new west.gui.Textfield().setLabel("Chat-Sichtbarkeit Inaktiv (in Prozent)").maxlength(3).setSize(3).setValue(Config.get("chat.fade"));
        var chatopacity = new west.gui.Textfield().setLabel("Chat-Sichtbarkeit (in Prozent)").maxlength(3).setSize(3).setValue(Config.get("chat.opacity"));
        add(chatsound);
        add(chatfade);
        add(chatopacity);
        this.content.append(new west.gui.Groupframe().appendToContentPane("<h2>" + "Chat" + "</h2>", "<hr/>", wrap).getMainDiv());
        this.saveBatch.push(function() {
            Config.set("soundsOff", chatsound.isSelected());
            var fade = parseInt(chatfade.getValue());
            if (fade > 100)
                fade = 100;
            else if (fade < 0)
                fade = 0;
            chatfade.setValue(fade);
            Config.set("chat.fade", fade);
            var opacity = parseInt(chatopacity.getValue());
            if (opacity > 100)
                opacity = 100;
            else if (opacity < 0)
                opacity = 0;
            chatopacity.setValue(opacity);
            Config.set("chat.opacity", opacity);
        });
        this.resetBatch.push(function() {
            chatfade.setValue(Config.reset("chat.fade"));
            chatopacity.setValue(Config.reset("chat.opacity"));
        });
    }
