    var prepareSellData = function(sellObj, itemData) {
        sellObj.currentBid = sellObj.current_bid || sellObj.auction_price;
        sellObj.itemLabel = new tw2widget.TableRowItem(itemData);
        sellObj.itemName = sellObj.itemLabel.obj.name;
        sellObj.bidder_name = sellObj.bidder_name || '';
        sellObj.singlePrice = sellObj.item_count > 1 ? parseInt(sellObj.currentBid / sellObj.item_count) : sellObj.currentBid;
        sellObj.singleMaxPrice = sellObj.item_count > 1 ? parseInt(sellObj.max_price / sellObj.item_count) : sellObj.max_price;
        sellObj.distance = marketDistance(sellObj.market_town_x, sellObj.market_town_y);
        if (sellObj.description != null && sellObj.description != '') {
            sellObj.description = sellObj.description.prepareTextblock(50);
            sellObj.descriptionImg = '<img src="https://westde.innogamescdn.com/images/infopoint.png" title="' + sellObj.description.escapeHTML() + '" />&nbsp;';
        } else
            sellObj.descriptionImg = '';
        if (sellObj.auction_ends_in <= 0) {
            sellObj.auction_ends_in = 0;
            if (sellObj.current_bid > 0) {
                sellObj.moneyIcon = '<img id="mps_marketFetchMoney_' + sellObj.market_offer_id + '" src="https://westde.innogamescdn.com/images/market/fetch_money.png" title="' + (!isInTown(sellObj.market_town_x, sellObj.market_town_y) ? 'Geld abholen' + ' (' + sellObj.market_town_name + ' - ' + 'Distanz' + ': ' + sellObj.distance + ')' : 'Geld abholen') + '" alt="" />';
                sellObj.offerIcon = '';
                sellObj.abortIcon = '';
            } else {
                sellObj.moneyIcon = '';
                sellObj.offerIcon = '<img id="mps_marketFetchOfferItem_' + sellObj.market_offer_id + '" src="https://westde.innogamescdn.com/images/market/fetch.png" title="' + (!isInTown(sellObj.market_town_x, sellObj.market_town_y) ? 'Artikel zurückholen' + ' (' + sellObj.market_town_name + ' - ' + 'Distanz' + ': ' + sellObj.distance + ')' : 'Artikel zurückholen') + '" alt="" />';
                sellObj.abortIcon = '';
            }
        } else {
            if (!isNaN(parseInt(sellObj.max_price)) && sellObj.current_bid == parseInt(sellObj.max_price)) {
                sellObj.auction_ends_in = 0;
                sellObj.moneyIcon = '<img id="mps_marketFetchMoney_' + sellObj.market_offer_id + '" src="https://westde.innogamescdn.com/images/market/fetch_money.png" title="' + (!isInTown(sellObj.market_town_x, sellObj.market_town_y) ? 'Geld abholen' + ' (' + sellObj.market_town_name + ' - ' + 'Distanz' + ': ' + sellObj.distance + ')' : 'Geld abholen') + '" alt="" />';
                sellObj.offerIcon = '';
                sellObj.abortIcon = '';
            } else {
                if (sellObj.current_bid > 0) {
                    sellObj.moneyIcon = '';
                    sellObj.offerIcon = '';
                    sellObj.abortIcon = '';
                } else {
                    sellObj.moneyIcon = '';
                    sellObj.offerIcon = '';
                    sellObj.abortIcon = '<img id="mps_marketAbortOffer_' + sellObj.market_offer_id + '" src="https://westde.innogamescdn.com/images/icons/delete.png" title="' + (!isInTown(sellObj.market_town_x, sellObj.market_town_y) ? 'Angebot zurückziehen' + ' (' + sellObj.market_town_name + ' - ' + 'Distanz' + ': ' + sellObj.distance + ')' : 'Angebot zurückziehen') + '" alt="" />';
                }
            }
        }
        return sellObj;
    }
