    var answers = [];
    var i, j;
    var sob = Survey.sob;
    for (i = 0; i < sob.questiongroups.length; i += 1) {
        var grp = sob.questiongroups[i];
        for (j = 0; j < grp.answers.length; j += 1) {
            var a = grp.answers[j];
            answers.push({
                answer_id: a.answer_id,
                result_value: Survey.window.$('#_answer_id_' + a.answer_id).prop('checked') ? 1 : 0
            })
        }
    }
