    FortBattleWindow.getSectorLabelMetadata = function(sectorCells) {
        const yValues = sectorCells.map( ([,y]) => y);
        const yModes = yValues.modes();
        let textY;
        if (yModes.length === 1) {
            textY = yModes[0];
        } else {
            const yMedian = yValues.median();
            yModes.sort( (a, b) => Math.abs(a - yMedian) - Math.abs(b - yMedian));
            const yMedianModesEnd = yModes.findIndex(y => Math.abs(y - yMedian) > Math.abs(yModes[0] - yMedian));
            const yMedianModes = yModes.slice(0, yMedianModesEnd === -1 ? undefined : yMedianModesEnd);
            textY = yMedianModes.reduce( (a, b) => a + b) / yMedianModes.length;
        }
        const xValues = sectorCells.filter( ([,y]) => y === Math.floor(textY)).map( ([x]) => x);
        return {
            leftEdge: Math.min(...xValues),
            rightEdge: Math.max(...xValues) + 1,
            verticalMiddle: textY,
            verticalSize: Math.max(...yValues) - Math.min(...yValues) + 1,
        };
    }
    FortBattleWindow.createSectorLabel = function(metadata, text, cellSize) {
        let fontSize, padding;
        if (metadata.rightEdge - metadata.leftEdge > 1) {
            fontSize = cellSize * 1.5 * (metadata.rightEdge - metadata.leftEdge) / text.length;
            padding = null;
        } else {
            fontSize = cellSize * metadata.verticalSize / text.length;
            padding = '0 ' + cellSize / 3 + 'px';
        }
        return $('<span></span>').css({
            fontSize: 'min(' + fontSize + 'px, ' + (cellSize - 1) + 'px)',
            lineHeight: 'min(' + fontSize + 'px, ' + cellSize + 'px)',
            padding: padding,
            top: metadata.verticalMiddle * cellSize + 'px',
            bottom: 'calc(100% - ' + (metadata.verticalMiddle + 1) * cellSize + 'px)',
            left: metadata.leftEdge * cellSize + 'px',
            right: 'calc(100% - ' + metadata.rightEdge * cellSize + 'px)',
        }).text(text);
    }
