        for (var i = 0; i < jobs.length; i++) {
            queue.push({
                title: 'Freigeschaltet',
                icon: 'https://westde.innogamescdn.com/images/jobs/' + jobs[i].shortname + '.png?1',
                subtitle: jobs[i].name
            });
        }
